#include "Lexer.h"#include "Parser.h"#include "Token.h"#include "Interpreter.h"#include <iostream>#include <fstream>#include <sstream>int main(int argc, char** argv) {    std::ifstream ifs;    std::string inputFile;    std::string theFullInput;    ifs.open (argv[1], std::ifstream::in);    while (getline(ifs,inputFile)) {        // std::istringstream myString(inputFile);       theFullInput += inputFile + '\n';    }    Lexer* lexer = new Lexer();    lexer->Run(theFullInput);    std::vector<Token*> tokensList = lexer->getTokenList();    Parser* myParserTest = new Parser(tokensList);    myParserTest->parse();    Interpreter* myInterpreterTest = new Interpreter(myParserTest->getDatalog());    myInterpreterTest->Start();    // TODO    delete lexer;    delete myParserTest;    delete myInterpreterTest;    ifs.close();    return 0;}